client/src/pages/student/exam-start.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { Clock, AlertTriangle, Play, Loader2, ArrowLeft, User, CheckCircle2 } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Checkbox } from "@/components/ui/checkbox";
import { StudentNavbar } from "@/components/layout/student-navbar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { format } from "date-fns";
import { uz } from "date-fns/locale";

interface StudentInfo {
  fullName: string;
  userId: string;
  groupName: string;
  facultyName: string;
}

interface ExamDetails {
  id: number;
  name: string;
  subjectName: string;
  teacherName: string;
  examDate: string;
  startTime: string;
  durationMinutes: number;
  questionsCount: number;
  status: string;
  canStart: boolean;
  studentInfo: StudentInfo;
}

export default function ExamStartPage() {
  const [, params] = useRoute("/talaba/imtihon/:examId/boshlash");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const examId = params?.examId;
  const [acceptedRules, setAcceptedRules] = useState(false);
  const [canStartNow, setCanStartNow] = useState(false);
  const [timeRemaining, setTimeRemaining] = useState<string>("");

  const { data: exam, isLoading, refetch } = useQuery<ExamDetails>({
    queryKey: ["/api/student/exam", examId],
    enabled: !!examId,
    refetchInterval: 10000,
  });

  useEffect(() => {
    if (!exam) return;

    const checkCanStart = () => {
      const now = new Date();
      const examDateTime = new Date(`${exam.examDate}T${exam.startTime}`);
      const canStart = exam.status === "active" || (now >= examDateTime && exam.status === "scheduled");
      setCanStartNow(canStart || exam.canStart);

      if (!canStart && !exam.canStart) {
        const diff = examDateTime.getTime() - now.getTime();
        if (diff > 0) {
          const hours = Math.floor(diff / (1000 * 60 * 60));
          const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
          const seconds = Math.floor((diff % (1000 * 60)) / 1000);
          if (hours > 0) {
            setTimeRemaining(`${hours} soat ${minutes} daqiqa ${seconds} soniya`);
          } else if (minutes > 0) {
            setTimeRemaining(`${minutes} daqiqa ${seconds} soniya`);
          } else {
            setTimeRemaining(`${seconds} soniya`);
          }
        }
      } else {
        setTimeRemaining("");
      }
    };

    checkCanStart();
    const interval = setInterval(checkCanStart, 1000);
    return () => clearInterval(interval);
  }, [exam]);

  const startExamMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", `/api/student/exam/${examId}/start`, {});
      return res.json();
    },
    onSuccess: () => {
      setLocation(`/talaba/imtihon/${examId}/session`);
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Xatolik!",
        description: error.message || "Imtihonni boshlashda xatolik",
      });
    },
  });

  const handleStart = () => {
    if (!acceptedRules) {
      toast({
        variant: "destructive",
        title: "Xatolik!",
        description: "Imtihon qoidalarini qabul qilishingiz kerak",
      });
      return;
    }
    startExamMutation.mutate();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <StudentNavbar />
        <main className="container mx-auto px-4 py-6 max-w-2xl">
          <Skeleton className="h-64 w-full" />
        </main>
      </div>
    );
  }

  if (!exam) {
    return (
      <div className="min-h-screen bg-background">
        <StudentNavbar />
        <main className="container mx-auto px-4 py-6 max-w-2xl">
          <Card>
            <CardContent className="p-6 text-center">
              <AlertTriangle className="w-16 h-16 text-destructive mx-auto mb-4" />
              <h2 className="text-xl font-semibold mb-2">Imtihon topilmadi</h2>
              <p className="text-muted-foreground mb-4">
                Ushbu imtihon mavjud emas yoki sizga ruxsat berilmagan
              </p>
              <Button onClick={() => setLocation("/talaba")}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Ortga qaytish
              </Button>
            </CardContent>
          </Card>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <StudentNavbar />
      <main className="container mx-auto px-4 py-6 max-w-2xl">
        <Button
          variant="ghost"
          onClick={() => setLocation("/talaba/imtihonlar")}
          className="mb-4"
          data-testid="button-back"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Ortga
        </Button>

        <Card className="mb-6">
          <CardHeader>
            <div className="flex items-start justify-between gap-4">
              <div>
                <CardTitle className="text-2xl">{exam.subjectName}</CardTitle>
                <p className="text-muted-foreground mt-1">{exam.name}</p>
              </div>
              <Badge variant={exam.status === "active" ? "default" : "outline"}>
                {exam.status === "active" ? "Faol" : "Rejalashtirilgan"}
              </Badge>
            </div>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="grid grid-cols-2 gap-4">
              <div className="p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">O'qituvchi</p>
                <p className="font-medium">{exam.teacherName}</p>
              </div>
              <div className="p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">Sana</p>
                <p className="font-medium">
                  {format(new Date(exam.examDate), "d-MMMM yyyy", { locale: uz })}
                </p>
              </div>
              <div className="p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">Davomiyligi</p>
                <p className="font-medium flex items-center gap-2">
                  <Clock className="w-4 h-4" />
                  {exam.durationMinutes} daqiqa
                </p>
              </div>
              <div className="p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">Savollar soni</p>
                <p className="font-medium">{exam.questionsCount} ta</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {exam.studentInfo && (
          <Card className="mb-6 border-primary/20 bg-primary/5">
            <CardHeader>
              <CardTitle className="text-lg flex items-center gap-2">
                <User className="w-5 h-5" />
                Shaxsni tasdiqlash
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">To'liq ism</p>
                  <p className="font-semibold">{exam.studentInfo.fullName}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">ID raqam</p>
                  <p className="font-semibold font-mono">{exam.studentInfo.userId}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Guruh</p>
                  <p className="font-semibold">{exam.studentInfo.groupName}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Fakultet</p>
                  <p className="font-semibold">{exam.studentInfo.facultyName}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        <Card className="mb-6">
          <CardContent className="pt-6">
            <h3 className="font-semibold mb-4 flex items-center gap-2">
              <AlertTriangle className="w-5 h-5 text-orange-500" />
              Imtihon qoidalari
            </h3>
            <ul className="space-y-3 text-sm">
              <li className="flex items-start gap-3 p-3 bg-muted rounded-lg">
                <CheckCircle2 className="w-5 h-5 text-primary mt-0.5 shrink-0" />
                <span>Imtihon davomiyligi <strong>{exam.durationMinutes} daqiqa</strong>. Vaqt tugaganda javoblar avtomatik yuboriladi.</span>
              </li>
              <li className="flex items-start gap-3 p-3 bg-muted rounded-lg">
                <CheckCircle2 className="w-5 h-5 text-primary mt-0.5 shrink-0" />
                <span>Imtihon to'liq ekran rejimida o'tkaziladi. Ekrandan chiqish <strong>qoidabuzarlik</strong> hisoblanadi.</span>
              </li>
              <li className="flex items-start gap-3 p-3 bg-muted rounded-lg">
                <CheckCircle2 className="w-5 h-5 text-primary mt-0.5 shrink-0" />
                <span>Boshqa oynaga o'tish (tab switching) <strong>taqiqlanadi</strong> va qayd etiladi.</span>
              </li>
              <li className="flex items-start gap-3 p-3 bg-muted rounded-lg">
                <CheckCircle2 className="w-5 h-5 text-primary mt-0.5 shrink-0" />
                <span>Nusxalash va qo'yish (copy-paste) <strong>bloklangan</strong>.</span>
              </li>
              <li className="flex items-start gap-3 p-3 bg-destructive/10 rounded-lg">
                <AlertTriangle className="w-5 h-5 text-destructive mt-0.5 shrink-0" />
                <span className="text-destructive font-medium">3 ta qoidabuzarlik = diskvalifikatsiya!</span>
              </li>
            </ul>

            <div className="mt-6 p-4 border rounded-lg">
              <div className="flex items-start gap-3">
                <Checkbox
                  id="accept-rules"
                  checked={acceptedRules}
                  onCheckedChange={(checked) => setAcceptedRules(checked as boolean)}
                  data-testid="checkbox-accept-rules"
                />
                <label htmlFor="accept-rules" className="text-sm cursor-pointer leading-relaxed">
                  Men <strong>{exam.studentInfo?.fullName || "talaba"}</strong>man va yuqoridagi imtihon qoidalarini o'qib chiqdim, ularga roziman.
                </label>
              </div>
            </div>
          </CardContent>
        </Card>

        {canStartNow ? (
          <div className="space-y-4">
            <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg">
              <CheckCircle2 className="w-8 h-8 text-green-600 mx-auto mb-2" />
              <p className="font-medium text-green-700 dark:text-green-400">
                Imtihon boshlashga tayyor!
              </p>
            </div>
            <Button
              className="w-full"
              size="lg"
              onClick={handleStart}
              disabled={!acceptedRules || startExamMutation.isPending}
              data-testid="button-start-exam"
            >
              {startExamMutation.isPending ? (
                <>
                  <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                  Yuklanmoqda...
                </>
              ) : (
                <>
                  <Play className="w-5 h-5 mr-2" />
                  Imtihonni boshlash
                </>
              )}
            </Button>
            {!acceptedRules && (
              <p className="text-center text-sm text-muted-foreground">
                Boshlash uchun yuqoridagi qoidalarni qabul qiling
              </p>
            )}
          </div>
        ) : (
          <div className="text-center p-6 bg-muted rounded-lg space-y-3">
            <Clock className="w-10 h-10 text-muted-foreground mx-auto" />
            <p className="text-muted-foreground">
              Imtihon hali boshlanmadi. Iltimos, belgilangan vaqtni kuting.
            </p>
            <div className="space-y-1">
              <p className="font-medium">
                Boshlanish vaqti: {exam.startTime}
              </p>
              {timeRemaining && (
                <p className="text-lg font-bold text-primary">
                  {timeRemaining} qoldi
                </p>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

client/src/pages/registrator/dashboard.t.content:
import { useQuery } from "@tanstack/react-query";
import { Building2, GraduationCap, Users, ClipboardCheck, Clock } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { AppSidebar } from "@/components/layout/sidebar";
import { Topbar } from "@/components/layout/topbar";
import { formatDistanceToNow } from "date-fns";
import { uz } from "date-fns/locale";

interface Stats {
  faculties: number;
  teachers: number;
  students: number;
  activeExams: number;
}

interface ActivityLog {
  id: number;
  action: string;
  userRole: string;
  details: Record<string, unknown>;
  createdAt: string;
}

interface UpcomingExam {
  id: number;
  name: string;
  subjectName: string;
  teacherName: string;
  examDate: string;
  startTime: string;
  status: string;
  groups: string[];
}

function StatCard({
  title,
  value,
  subtitle,
  icon: Icon,
  iconBgColor,
  pulse,
}: {
  title: string;
  value: number | string;
  subtitle: string;
  icon: typeof Building2;
  iconBgColor: string;
  pulse?: boolean;
}) {
  return (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div
            className={`w-10 h-10 rounded-full flex items-center justify-center ${iconBgColor}`}
          >
            <Icon className="w-5 h-5 text-white" />
          </div>
          {pulse && (
            <span className="relative flex h-3 w-3">
              <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-green-400 opacity-75"></span>
              <span className="relative inline-flex rounded-full h-3 w-3 bg-green-500"></span>
            </span>
          )}
        </div>
        <div className="mt-4">
          <p className="text-sm text-muted-foreground">{title}</p>
          <p className="text-2xl font-bold mt-1" data-testid={`stat-${title.toLowerCase().replace(/ /g, "-")}`}>
            {value}
          </p>
          <p className="text-xs text-muted-foreground mt-1">{subtitle}</p>
        </div>
      </CardContent>
    </Card>
  );
}

function StatCardSkeleton() {
  return (
    <Card>
      <CardContent className="p-6">
        <Skeleton className="w-10 h-10 rounded-full" />
        <div className="mt-4 space-y-2">
          <Skeleton className="h-4 w-24" />
          <Skeleton className="h-8 w-16" />
          <Skeleton className="h-3 w-20" />
        </div>
      </CardContent>
    </Card>
  );
}

export default function RegistratorDashboard() {
  const { data: stats, isLoading: statsLoading } = useQuery<Stats>({
    queryKey: ["/api/stats"],
  });

  const { data: activities, isLoading: activitiesLoading } = useQuery<ActivityLog[]>({
    queryKey: ["/api/activity-logs"],
  });

  const { data: upcomingExams, isLoading: examsLoading } = useQuery<UpcomingExam[]>({
    queryKey: ["/api/exams/upcoming"],
  });

  const getStatusBadge = (status: string) => {
    const variants: Record<string, { variant: "default" | "secondary" | "destructive" | "outline"; label: string }> = {
      scheduled: { variant: "outline", label: "Rejalashtirilgan" },
      active: { variant: "default", label: "Faol" },
      completed: { variant: "secondary", label: "Yakunlangan" },
      draft: { variant: "outline", label: "Qoralama" },
    };
    const { variant, label } = variants[status] || { variant: "outline" as const, label: status };
    return <Badge variant={variant}>{label}</Badge>;
  };

  return (
    <div className="flex min-h-screen bg-background">
      <AppSidebar />
      <div className="flex-1 ml-[260px]">
        <Topbar title="Bosh sahifa" />
        <main className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {statsLoading ? (
              <>
                <StatCardSkeleton />
                <StatCardSkeleton />
                <StatCardSkeleton />
                <StatCardSkeleton />
              </>
            ) : (
              <>
                <StatCard
                  title="Fakultetlar"
                  value={stats?.faculties || 0}
                  subtitle="ta fakultet"
                  icon={Building2}
                  iconBgColor="bg-blue-500"
                />
                <StatCard
                  title="O'qituvchilar"
                  value={stats?.teachers || 0}
                  subtitle="ta o'qituvchi"
                  icon={GraduationCap}
                  iconBgColor="bg-green-500"
                />
                <StatCard
                  title="Talabalar"
                  value={stats?.students || 0}
                  subtitle="ta talaba"
                  icon={Users}
                  iconBgColor="bg-purple-500"
                />
                <StatCard
                  title="Faol imtihonlar"
                  value={stats?.activeExams || 0}
                  subtitle="ta imtihon"
                  icon={ClipboardCheck}
                  iconBgColor="bg-orange-500"
                  pulse={(stats?.activeExams || 0) > 0}
                />
              </>
            )}
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">So'nggi faoliyat</CardTitle>
              </CardHeader>
              <CardContent>
                {activitiesLoading ? (
                  <div className="space-y-3">
                    {[1, 2, 3, 4, 5].map((i) => (
                      <div key={i} className="flex items-center gap-3">
                        <Skeleton className="w-8 h-8 rounded-full" />
                        <div className="flex-1 space-y-1">
                          <Skeleton className="h-4 w-3/4" />
                          <Skeleton className="h-3 w-1/4" />
                        </div>
                      </div>
                    ))}
                  </div>
                ) : activities && activities.length > 0 ? (
                  <div className="space-y-3">
                    {activities.slice(0, 10).map((activity) => (
                      <div
                        key={activity.id}
                        className="flex items-center gap-3 py-2 border-b border-border last:border-0"
                        data-testid={`activity-${activity.id}`}
                      >
                        <div className="w-8 h-8 rounded-full bg-muted flex items-center justify-center">
                          <Clock className="w-4 h-4 text-muted-foreground" />
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="text-sm truncate">{activity.action}</p>
                          <p className="text-xs text-muted-foreground">
                            {formatDistanceToNow(new Date(activity.createdAt), {
                              addSuffix: true,
                              locale: uz,
                            })}
                          </p>
                        </div>
                        <Badge variant="outline" className="text-xs">
                          {activity.userRole}
                        </Badge>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-muted-foreground">
                    <Clock className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>Hozircha faoliyat yo'q</p>
                  </div>
                )}
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Yaqinlashayotgan imtihonlar</CardTitle>
              </CardHeader>
              <CardContent>
                {examsLoading ? (
                  <div className="space-y-3">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="p-3 border rounded-lg">
                        <Skeleton className="h-4 w-1/2 mb-2" />
                        <Skeleton className="h-3 w-3/4 mb-1" />
                        <Skeleton className="h-3 w-1/4" />
                      </div>
                    ))}
                  </div>
                ) : upcomingExams && upcomingExams.length > 0 ? (
                  <div className="space-y-3">
                    {upcomingExams.slice(0, 5).map((exam) => (
                      <div
                        key={exam.id}
                        className="p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                        data-testid={`exam-${exam.id}`}
                      >
                        <div className="flex items-start justify-between gap-2">
                          <div className="min-w-0">
                            <p className="font-medium truncate">{exam.subjectName}</p>
                            <p className="text-sm text-muted-foreground truncate">
                              {exam.teacherName}
                            </p>
                          </div>
                          {getStatusBadge(exam.status)}
                        </div>
                        <div className="flex items-center gap-4 mt-2 text-xs text-muted-foreground">
                          <span>{exam.examDate}</span>
                          <span>{exam.startTime}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-muted-foreground">
                    <ClipboardCheck className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>Yaqinlashayotgan imtihon yo'q</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </main>
      </div>
    </div>
  );
}

client/src/pages/registrator/subjects.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Pencil, Trash2, BookOpen } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { AppSidebar } from "@/components/layout/sidebar";
import { Topbar } from "@/components/layout/topbar";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { Subject, Department, Faculty } from "@shared/schema";

interface SubjectWithStats extends Subject {
  departmentName: string;
  facultyName: string;
  teachersCount: number;
}

export default function SubjectsPage() {
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [isDeleteOpen, setIsDeleteOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState<SubjectWithStats | null>(null);
  const [formData, setFormData] = useState({ name: "", code: "", credits: "3", facultyId: "", departmentId: "" });
  const { toast } = useToast();

  const { data: subjects, isLoading } = useQuery<SubjectWithStats[]>({
    queryKey: ["/api/subjects"],
  });

  const { data: faculties } = useQuery<Faculty[]>({
    queryKey: ["/api/faculties"],
  });

  const { data: departments } = useQuery<Department[]>({
    queryKey: ["/api/departments"],
  });

  const filteredDepartments = departments?.filter(
    (dept) => !formData.facultyId || dept.facultyId === parseInt(formData.facultyId)
  );

  const createMutation = useMutation({
    mutationFn: async (data: { name: string; code: string; credits: number; departmentId: number }) => {
      return apiRequest("POST", "/api/subjects", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subjects"] });
      setIsAddOpen(false);
      setFormData({ name: "", code: "", credits: "3", facultyId: "", departmentId: "" });
      toast({ title: "Muvaffaqiyatli!", description: "Fan qo'shildi" });
    },
    onError: () => {
      toast({ variant: "destructive", title: "Xatolik!", description: "Fan qo'shishda xatolik" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: { name: string; code: string; credits: number; departmentId: number } }) => {
      return apiRequest("PATCH", `/api/subjects/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subjects"] });
      setIsEditOpen(false);
      setSelectedSubject(null);
      toast({ title: "Muvaffaqiyatli!", description: "Fan yangilandi" });
    },
    onError: () => {
      toast({ variant: "destructive", title: "Xatolik!", description: "Fan yangilashda xatolik" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest("DELETE", `/api/subjects/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subjects"] });
      setIsDeleteOpen(false);
      setSelectedSubject(null);
      toast({ title: "Muvaffaqiyatli!", description: "Fan o'chirildi" });
    },
    onError: () => {
      toast({ variant: "destructive", title: "Xatolik!", description: "Fan o'chirishda xatolik" });
    },
  });

  const handleAdd = () => {
    setFormData({ name: "", code: "", credits: "3", facultyId: "", departmentId: "" });
    setIsAddOpen(true);
  };

  const handleEdit = (subject: SubjectWithStats) => {
    const dept = departments?.find((d) => d.id === subject.departmentId);
    setSelectedSubject(subject);
    setFormData({
      name: subject.name,
      code: subject.code,
      credits: (subject.credits || 3).toString(),
      facultyId: dept?.facultyId?.toString() || "",
      departmentId: subject.departmentId?.toString() || "",
    });
    setIsEditOpen(true);
  };

  const handleDelete = (subject: SubjectWithStats) => {
    setSelectedSubject(subject);
    setIsDeleteOpen(true);
  };

  const handleSubmitAdd = () => {
    if (!formData.name || !formData.code || !formData.departmentId) {
      toast({ variant: "destructive", title: "Xatolik!", description: "Barcha maydonlarni to'ldiring" });
      return;
    }
    createMutation.mutate({
      name: formData.name,
      code: formData.code,
      credits: parseInt(formData.credits),
      departmentId: parseInt(formData.departmentId),
    });
  };

  const handleSubmitEdit = () => {
    if (!selectedSubject || !formData.name || !formData.code) return;
    updateMutation.mutate({
      id: selectedSubject.id,
      data: {
        name: formData.name,
        code: formData.code,
        credits: parseInt(formData.credits),
        departmentId: parseInt(formData.departmentId),
      },
    });
  };

  const handleConfirmDelete = () => {
    if (!selectedSubject) return;
    deleteMutation.mutate(selectedSubject.id);
  };

  return (
    <div className="flex min-h-screen bg-background">
      <AppSidebar />
      <div className="flex-1 ml-[260px]">
        <Topbar title="Fanlar" />
        <main className="p-6">
          <div className="flex items-center justify-between gap-4 mb-6 flex-wrap">
            <h2 className="text-2xl font-semibold" data-testid="text-page-heading">Fanlar</h2>
            <Button onClick={handleAdd} data-testid="button-add-subject">
              <Plus className="w-4 h-4 mr-2" />
              Fan qo'shish
            </Button>
          </div>

          <Card>
            <CardContent className="p-0">
              {isLoading ? (
                <div className="p-6 space-y-4">
                  {[1, 2, 3, 4, 5].map((i) => (
                    <Skeleton key={i} className="h-12 w-full" />
                  ))}
                </div>
              ) : subjects && subjects.length > 0 ? (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Kod</TableHead>
                      <TableHead>Fan nomi</TableHead>
                      <TableHead>Yo'nalish</TableHead>
                      <TableHead>Fakultet</TableHead>
                      <TableHead className="text-center">Kreditlar</TableHead>
                      <TableHead className="text-center">O'qituvchilar</TableHead>
                      <TableHead className="text-right">Harakatlar</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {subjects.map((subject) => (
                      <TableRow key={subject.id} data-testid={`row-subject-${subject.id}`}>
                        <TableCell className="font-mono font-medium">{subject.code}</TableCell>
                        <TableCell>{subject.name}</TableCell>
                        <TableCell>{subject.departmentName || "-"}</TableCell>
                        <TableCell>{subject.facultyName || "-"}</TableCell>
                        <TableCell className="text-center">
                          <Badge variant="outline">{subject.credits}</Badge>
                        </TableCell>
                        <TableCell className="text-center">{subject.teachersCount}</TableCell>
                        <TableCell className="text-right">
                          <div className="flex items-center justify-end gap-2">
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => handleEdit(subject)}
                              data-testid={`button-edit-${subject.id}`}
                            >
                              <Pencil className="w-4 h-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="icon"
                              className="text-destructive hover:text-destructive"
                              onClick={() => handleDelete(subject)}
                              data-testid={`button-delete-${subject.id}`}
                            >
                              <Trash2 className="w-4 h-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              ) : (
                <div className="flex flex-col items-center justify-center py-16">
                  <BookOpen className="w-16 h-16 text-muted-foreground/50 mb-4" />
                  <p className="text-lg font-medium text-muted-foreground mb-2">
                    Hozircha fan yo'q
                  </p>
                  <p className="text-sm text-muted-foreground mb-4">
                    Fan qo'shish uchun tugmani bosing
                  </p>
                  <Button onClick={handleAdd} data-testid="button-add-subject-empty">
                    <Plus className="w-4 h-4 mr-2" />
                    Fan qo'shish
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </main>
      </div>

      <Dialog open={isAddOpen} onOpenChange={setIsAddOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Fan qo'shish</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Fan nomi *</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="Matematika"
                data-testid="input-subject-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="code">Kod *</Label>
              <Input
                id="code"
                value={formData.code}
                onChange={(e) => setFormData({ ...formData, code: e.target.value.toUpperCase() })}
                placeholder="MAT101"
                data-testid="input-subject-code"
              />
            </div>
            <div className="space-y-2">
              <Label>Fakultet *</Label>
              <Select value={formData.facultyId} onValueChange={(v) => setFormData({ ...formData, facultyId: v, departmentId: "" })}>
                <SelectTrigger data-testid="select-faculty">
                  <SelectValue placeholder="Fakultet tanlang" />
                </SelectTrigger>
                <SelectContent>
                  {faculties?.map((f) => (
                    <SelectItem key={f.id} value={f.id.toString()}>{f.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Yo'nalish *</Label>
              <Select value={formData.departmentId} onValueChange={(v) => setFormData({ ...formData, departmentId: v })}>
                <SelectTrigger data-testid="select-department">
                  <SelectValue placeholder="Yo'nalish tanlang" />
                </SelectTrigger>
                <SelectContent>
                  {filteredDepartments?.map((d) => (
                    <SelectItem key={d.id} value={d.id.toString()}>{d.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="credits">Kreditlar</Label>
              <Select value={formData.credits} onValueChange={(v) => setFormData({ ...formData, credits: v })}>
                <SelectTrigger data-testid="select-credits">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {[1, 2, 3, 4, 5, 6].map((c) => (
                    <SelectItem key={c} value={c.toString()}>{c}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsAddOpen(false)} data-testid="button-cancel">
              Bekor qilish
            </Button>
            <Button onClick={handleSubmitAdd} disabled={createMutation.isPending} data-testid="button-save">
              {createMutation.isPending ? "Saqlanmoqda..." : "Saqlash"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog open={isEditOpen} onOpenChange={setIsEditOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Fanni tahrirlash</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="edit-name">Fan nomi *</Label>
              <Input
                id="edit-name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                data-testid="input-edit-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="edit-code">Kod *</Label>
              <Input
                id="edit-code"
                value={formData.code}
                onChange={(e) => setFormData({ ...formData, code: e.target.value.toUpperCase() })}
                data-testid="input-edit-code"
              />
            </div>
            <div className="space-y-2">
              <Label>Fakultet *</Label>
              <Select value={formData.facultyId} onValueChange={(v) => setFormData({ ...formData, facultyId: v, departmentId: "" })}>
                <SelectTrigger data-testid="select-edit-faculty">
                  <SelectValue placeholder="Fakultet tanlang" />
                </SelectTrigger>
                <SelectContent>
                  {faculties?.map((f) => (
                    <SelectItem key={f.id} value={f.id.toString()}>{f.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Yo'nalish *</Label>
              <Select value={formData.departmentId} onValueChange={(v) => setFormData({ ...formData, departmentId: v })}>
                <SelectTrigger data-testid="select-edit-department">
                  <SelectValue placeholder="Yo'nalish tanlang" />
                </SelectTrigger>
                <SelectContent>
                  {filteredDepartments?.map((d) => (
                    <SelectItem key={d.id} value={d.id.toString()}>{d.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Kreditlar</Label>
              <Select value={formData.credits} onValueChange={(v) => setFormData({ ...formData, credits: v })}>
                <SelectTrigger data-testid="select-edit-credits">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {[1, 2, 3, 4, 5, 6].map((c) => (
                    <SelectItem key={c} value={c.toString()}>{c}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditOpen(false)} data-testid="button-edit-cancel">
              Bekor qilish
            </Button>
            <Button onClick={handleSubmitEdit} disabled={updateMutation.isPending} data-testid="button-edit-save">
              {updateMutation.isPending ? "Saqlanmoqda..." : "Saqlash"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <AlertDialog open={isDeleteOpen} onOpenChange={setIsDeleteOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Fanni o'chirish</AlertDialogTitle>
            <AlertDialogDescription>
              "{selectedSubject?.name}" fanini o'chirmoqchimisiz? Bu amalni qaytarib bo'lmaydi.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-delete-cancel">Bekor qilish</AlertDialogCancel>
            <AlertDialogAction
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              onClick={handleConfirmDelete}
              disabled={deleteMutation.isPending}
              data-testid="button-delete-confirm"
            >
              {deleteMutation.isPending ? "O'chirilmoqda..." : "O'chirish"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

