      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Javobni saqlashda xatolik" });
    }
  });

  app.post("/api/student/exam-session/:sessionId/violation", requireAuth, requireRole("talaba"), async (req, res) => {
